mapper.insert:
INSERT INTO 
WYDUSER(USERID
	  , USERPW
	  , USERNAME
	  , USEREMAIL)
VALUES(#{userId}
	 , #{userPw}
	 , #{userName}
	 , #{userEmail})

mapper.select:
UPDATE WYDUSER
   SET USERID = #{userId}
   	 , USERPW = #{userPw}
   	 , USERNAME = #{userName}
   	 , USEREMAIL = #{userEmail}
 WHERE USERID = #{userId}
